function submitWin(roomName,stageName,token){console.log(`Victoria en: ${roomName}/${stageName}. Enviando al servidor...`);const form=document.createElement('form');form.method='POST';form.action=`/win/${roomName}/${stageName}`;const tokenInput=document.createElement('input');tokenInput.type='hidden';tokenInput.name='token';tokenInput.value=token;form.appendChild(tokenInput);document.body.appendChild(form);form.submit();}
function failGame(message,roomName){const overlay=document.createElement('div');overlay.style.position='fixed';overlay.style.top='0';overlay.style.left='0';overlay.style.width='100%';overlay.style.height='100%';overlay.style.backgroundColor='rgba(0, 0, 0, 0.7)';overlay.style.display='flex';overlay.style.justifyContent='center';overlay.style.alignItems='center';overlay.style.zIndex='10000';const modal=document.createElement('div');modal.style.backgroundColor='#15202b';modal.style.color='white';modal.style.padding='30px';modal.style.borderRadius='10px';modal.style.textAlign='center';modal.style.border='2px solid #0891b2';const title=document.createElement('h2');title.innerText='Has Perdido';title.style.marginBottom='15px';const text=document.createElement('p');text.innerText=message;text.style.marginBottom='25px';text.style.fontSize='1.1rem';const retryButton=document.createElement('button');retryButton.innerText='Reintentar';retryButton.className='btn btn-lg btn-primary';retryButton.onclick=function(){window.location.reload();};modal.appendChild(title);modal.appendChild(text);modal.appendChild(retryButton);overlay.appendChild(modal);document.body.appendChild(overlay);}