function initRealOrAIGame(roomName,stageName,winToken){const gameImage=document.getElementById('game-image');const livesDisplay=document.getElementById('lives-display');const scoreDisplay=document.getElementById('score-display');const btnReal=document.getElementById('btn-real');const btnIA=document.getElementById('btn-ia');const feedbackOverlay=document.getElementById('feedback-overlay');const TOTAL_LIVES=3;const IMAGES_TO_WIN=10;const IMAGE_PATH='/static/img/ai_game/';let isGameActive=false;async function startGame(){isGameActive=true;setButtonsDisabled(true);try{const response=await fetch('/api/ai/real_or_ai/start',{method:'POST'});if(!response.ok)throw new Error('Failed to start game');const data=await response.json();updateScoreUI(0);updateLivesUI(3);gameImage.src=IMAGE_PATH+data.image_file;setButtonsDisabled(false);}catch(error){console.error("Error starting game:",error);feedbackOverlay.textContent='Error al iniciar';feedbackOverlay.style.color='var(--incorrect)';feedbackOverlay.style.display='flex';}}
function setButtonsDisabled(disabled){btnReal.disabled=disabled;btnIA.disabled=disabled;}
async function handleAnswer(guess){if(!isGameActive)return;setButtonsDisabled(true);try{const response=await fetch('/api/ai/real_or_ai/guess',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({guess:guess})});if(!response.ok)throw new Error('Failed to submit guess');const data=await response.json();showFeedback(data.correct);updateLivesUI(data.new_lives);updateScoreUI(data.new_score);setTimeout(()=>{feedbackOverlay.style.display='none';if(data.game_over){endGame(data.win);}else{gameImage.src=IMAGE_PATH+data.next_image_file;setButtonsDisabled(false);}},1200);}catch(error){console.error("Error submitting guess:",error);feedbackOverlay.textContent='Error de conexiÃ³n';feedbackOverlay.style.display='flex';setButtonsDisabled(false);}}
function showFeedback(isCorrect){feedbackOverlay.textContent=isCorrect?'âœ”ï¸ Correcto':'âŒ Incorrecto';feedbackOverlay.style.color=isCorrect?'var(--correct)':'var(--incorrect)';feedbackOverlay.style.display='flex';}
function updateLivesUI(currentLives){livesDisplay.textContent='â¤ï¸'.repeat(currentLives)+'ðŸ–¤'.repeat(TOTAL_LIVES-currentLives);}
function updateScoreUI(currentScore){scoreDisplay.textContent=`Correctas: ${currentScore} / ${IMAGES_TO_WIN}`;}
function endGame(didWin){isGameActive=false;setButtonsDisabled(true);if(didWin){feedbackOverlay.textContent='ðŸ† Â¡GANASTE!';feedbackOverlay.style.color='gold';feedbackOverlay.style.display='flex';setTimeout(()=>submitWin(roomName,stageName,winToken),2000);}else{const message="No has superado la prueba. La IA te ha engaÃ±ado.";failGame(message,roomName);}}
btnReal.addEventListener('click',()=>handleAnswer('real'));btnIA.addEventListener('click',()=>handleAnswer('ia'));startGame();}